mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT POST_NO , ID , NICKNAME , STATUS_CODE , PHONE , POST_TITLE , POST_CONTENT , VIEW_COUNT , VOTER_COUNT , VOTE_START_DATE , VOTE_END_DATE , FUND_TARGET_PAY , VOTE_TARGET_COUNT , FUND_TARGET_DAY , SPONSOR_COUNT , FUND_PAY , FUND_START_DATE , FUND_END_DATE FROM STORYFUNDING
POST_TITLE = #{searchKeyword}
NICKNAME = #{searchKeyword}
ORDER BY POST_NO ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE STORYFUNDING
PHONE = #{phone:VARCHAR} , POST_TITLE = #{postTitle:VARCHAR}, POST_CONTENT = #{postContent:VARCHAR} , FUND_TARGET_PAY = #{fundTargetPay:NUMERIC}, FUND_TARGET_DAY = #{fundTargetDay:NUMERIC}, VOTE_TARGET_COUNT = #{fundTargetPay:NUMERIC}*0.001, FUND_END_DATE = SYSDATE+31+#{fundTargetDay:NUMERIC}
WHERE POST_NO IN #{postNo}

		UPDATE STORYFUNDING
		
			STATUS_CODE = #{statusCode:CHAR},
		
		WHERE POST_NO IN #{postNo:VARCHAR}

mapper.delete:
DELETE
FROM STORYFUNDING
WHERE POST_NO IN #{value}
