mapper.update:
UPDATE message
message_status = #{messageStatus}, receive_date = SYSDATE
WHERE message_no=#{messageNo}

mapper.select:
SELECT send_id, receive_id1, message_title, send_date, receive_date
FROM( SELECT inner_table.*, ROWNUM AS row_seq
	FROM( SELECT send_id, receive_id1, message_title, send_date, receive_date
		FROM message
		
			
				
					send_id=#{searchKeyword}
				
				
					receive_id=#{searchKeyword}
				
				
					message_title=#{searchKeyword}
				
			
		
		ORDER BY send_id ) inner_table
	WHERE ROWNUM <=#{endRowNum} )
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM(	SELECT send_id, receive_id1, message_title, send_date, receive_date
	FROM message
	
		
				
					send_id=#{searchKeyword}
				
				
					receive_id=#{searchKeyword}
				
				
					message_title=#{searchKeyword}
				
			
	 ) countTable
