mapper.update:
UPDATE product
prod_name = #{prodName}, price = #{price}, country = #{country}, prod_detail = #{prodDetail}, company = #{company}, manu_date = #{manuDate}, quantity = #{quantity}, discount_price = #{discountPrice}
WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  		FROM (	SELECT prod_no, prod_name, price, discount_price
  						 FROM product 	  					 
								
											
												
										 			prod_name = #{searchKeyword}
												
												
										 			price = #{searchKeyword}
												
											
										) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT prod_no, prod_name, price, country, prod_detail, company, manu_date, quantity, discount_price
	FROM product
					
											
												
										 			prod_name = #{searchKeyword}
												
												
										 			price = #{searchKeyword}
												
											
										 ) countTable

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  		FROM (	SELECT prod_no, prod_name, price, country, company, manu_date, reg_date, quantity ,discount_price, prod_code
  						 FROM product 	  					 
								
											
												
										 			prod_name = #{searchKeyword}
												
												
										 			price = #{searchKeyword}
												
											
										) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
