SELECT report_no, reporter_id, reported_id, report_category, report_date
FROM( SELECT inner_table.*, ROWNUM AS row_seq
	FROM( SELECT report_no, reporter_id, reported_id, report_category, report_date
		FROM report
		
			
				
					reporter_id=#{searchKeyword}
				
				
					reported_id=#{searchKeyword}
				
				
					report_category=#{searchKeyword}
				
				
					report_date=#{searchKeyword}
				
			
		
		ORDER BY id ) inner_table
	WHERE ROWNUM <=#{endRowNum} )
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM(	SELECT mh.send_id, mh.receive_id, m.message_title, mh.send_date, mh.receive_date
	FROM message m, message_history hm
	WHERE mh.message_no=m.message_no
	
		
				
					reporter_id=#{searchKeyword}
				
				
					reported_id=#{searchKeyword}
				
				
					report_category=#{searchKeyword}
				
				
					report_date=#{searchKeyword}
				
			
	 ) countTable
